import { ApiProperty } from '@nestjs/swagger';
import { Expose } from 'class-transformer';
import { IsNotEmpty, IsOptional, IsString, MaxLength } from 'class-validator';

// const productWithParameters = Prisma.validator<Prisma.productsArgs>()({
//   include: {
//     price: new Prisma.Decimal(price)
//   }
// });

// type ProductWithParameters = Prisma.productsGetPayload<
//   typeof productWithParameters
// >;

export class ProductDto {
  /**
   * The internal autogenerated id
   * @example 1
   */
  @Expose()
  @ApiProperty({
    description: 'The internal autogenerated id of the product'
  })
  productId: number;

  @IsString()
  @IsNotEmpty()
  @Expose()
  @MaxLength(8)
  @ApiProperty({
    example: 'UREN',
    description: 'The product code'
  })
  code: string;

  @IsString()
  @IsNotEmpty()
  @Expose()
  @MaxLength(148)
  @ApiProperty({
    example: 'Cleaning Service',
    description: 'The product name'
  })
  name: string;

  @IsString()
  @IsNotEmpty()
  @Expose()
  @MaxLength(224)
  @ApiProperty({
    example: 'Cleaning Service',
    description: 'The product description'
  })
  description: string;

  @IsString()
  @Expose()
  @ApiProperty({
    example: 'UREN: Cleaning Service',
    description: 'Is the Cname of the product with its code'
  })
  get nameWithCode() {
    return `${this.code}: ${this.name}`;
  }

  @Expose()
  @ApiProperty({
    example: '12.5',
    description: 'The product name'
  })
  price: number;

  @Expose()
  @ApiProperty({
    description: 'The creation date of the product'
  })
  createdAt: Date;

  @Expose()
  @ApiProperty({
    description: 'The user who created the product'
  })
  createdBy: string;

  @Expose()
  @ApiProperty({
    description: 'The last update date of the product'
  })
  updatedAt: Date;

  @Expose()
  @ApiProperty({
    description: 'The user who updated the product'
  })
  updatedBy: string;
}
